function main({ data1 }) {
  try {
    // 解析输入(如果是字符串)
    const responseBody = typeof data1 === 'string' ? JSON.parse(data1) : data1;

    // 检查响应体结构
    if (!responseBody || !responseBody.data) {
      return { 
        result: "错误：输入格式无效，需要包含 data 字段"
      };
    }

    // 解析 data 字段(它是一个 JSON 字符串)
    const parsedData = JSON.parse(responseBody.data);

    // 检查解析后的数据结构
    if (!parsedData || !parsedData.output || !Array.isArray(parsedData.output)) {
      return { 
        result: "错误：data 字段格式无效，需要包含 output 数组"
      };
    }

    // 创建 Markdown 表格头部
    let markdownTable = "| 话题 | 浏览量 |\n| :--- | ---: |\n";

    // 处理每个数据项
    for (let item of parsedData.output) {
      try {
        // 解析每个项(它们也是 JSON 字符串)
        const record = JSON.parse(item);

        // 添加到表格中
        markdownTable += `| ${record.name} | ${record.view_num.replace(/\B(?=(\d{3})+(?!\d))/g, ",")} |\n`;
      } catch (e) {
        // 如果解析失败，尝试使用正则表达式提取数据
        if (typeof item === 'string') {
          try {
            const nameMatch = item.match(/"name":"([^"]*)"/);
            const viewMatch = item.match(/"view_num":"([^"]*)"/);

            if (nameMatch && viewMatch) {
              const formattedNumber = viewMatch[1].replace(/\B(?=(\d{3})+(?!\d))/g, ",");
              markdownTable += `| ${nameMatch[1]} | ${formattedNumber} |\n`;
            } else {
              markdownTable += `| 解析错误 | 错误 |\n`;
            }
          } catch (regexError) {
            markdownTable += `| 解析错误 | 错误 |\n`;
          }
        } else {
          markdownTable += `| 解析错误 | 错误 |\n`;
        }
      }
    }

    return { 
      result: markdownTable
    };
  } catch (e) {
    return { 
      result: `错误: ${e.message}`
    };
  }
}

