function main({ responseBody }) {
    try {
        // 解析响应体 - 支持字符串或已解析的对象
        const response = typeof responseBody === 'string' ? JSON.parse(responseBody) : responseBody;

        // 解析 data 字段中的 JSON 字符串
        const dataObj = JSON.parse(response.data);

        // 创建存储结果的数组
        const articles = [];

        // 预填充 20 个空文章对象
        for (let i = 0; i < 20; i++) {
            articles.push({ title: "", content: "" });
        }

        // 遍历 output 数组提取标题和正文，最多处理 20 组
        if (dataObj.output && Array.isArray(dataObj.output)) {
            // 处理最多 20 个项目
            for (let i = 0; i < Math.min(20, dataObj.output.length); i++) {
                const item = dataObj.output[i];
                const parts = item.split(/\n\s*正文：/);

                let title = "";
                let content = "";

                if (parts.length > 1) {
                    // 有标题和正文的情况
                    title = parts[0].replace('标题：', '').trim();
                    content = parts[1].trim();
                } else if (item.startsWith('标题：')) {
                    // 只有标题的情况
                    title = item.replace('标题：', '').trim();
                } else if (item.includes('正文：')) {
                    // 只有正文的情况
                    content = item.replace('正文：', '').trim();
                } else {
                    // 没有标记的情况，整个文本作为正文
                    content = item.trim();
                }

                // 更新数组中的相应文章
                articles[i] = {
                    title: title,
                    content: content
                };
            }
        }

        // 返回解析结果
        return {
            articles: articles,
            debug_url: response.debug_url
        };
    } catch (error) {
        // 处理解析错误
        return {
            error: `解析失败: ${error.message}`,
            originalResponse: responseBody
        };
    }
}

