function main({ articles, result }) {
    // 首先过滤出 result 值为"1"的文章（保留商业软文）
    // 如果未提供 articles，则直接返回空结果
    if (!articles || !Array.isArray(articles) || !result || !Array.isArray(result)) {
        return {
            error: "输入无效：需要提供文章数组和结果数组",
            good_article1: "none",
            good_article2: "none",
            good_article3: "none",
            good_article4: "none",
            good_article5: "none"
        };
    }

    // 只保留那些对应 result 值为"1"的文章
    const filteredArticles = articles.filter((article, index) => 
        index < result.length && result[index] === "1");

    // 创建结果对象，默认所有文章为"none"
    const output = {
        good_article1: "none",
        good_article2: "none",
        good_article3: "none",
        good_article4: "none",
        good_article5: "none"
    };

    // 如果过滤后没有文章，直接返回全"none"结果
    if (filteredArticles.length === 0) {
        return output;
    }

    // 随机洗牌数组 (Fisher-Yates 算法)
    const shuffledArticles = [...filteredArticles];
    for (let i = shuffledArticles.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [shuffledArticles[i], shuffledArticles[j]] = [shuffledArticles[j], shuffledArticles[i]];
    }

    // 选择前 5篇文章(或更少)
    const selectedCount = Math.min(5, shuffledArticles.length);
    for (let i = 0; i < selectedCount; i++) {
        output[`good_article${i+1}`] = shuffledArticles[i];
    }

    return output;
}

